import React, { useState, useEffect } from 'react';
// import "./App.css";
export const RollTrailerManagementSystemSearch = () => {
    const [error, setError] = useState(null);
    const [loaded, setLoaded] = useState(false);
    const [items, setItems] = useState([]);


    // search component 
    const [query, setQuery] = useState("");

    useEffect(() => {
        const request_headers = new Headers();
        const api_key = "BAyhM5mX1WVeNNQhUUBJkBEiknlTwSAIPhd4AbZK";
        request_headers.append("Authorization", `Bearer ${api_key}`);
        request_headers.append("Content-Type", "application/json");

        const request_options = {
            method: "GET",
            headers: request_headers,
        };

        fetch("https://countryapi.io/api/all", request_options)
            .then((res) => res.json())
            .then(
                (result) => {
                    setLoaded(true);
                    setItems(result);
                },
                (error) => {
                    setLoaded(true);
                    setError(error);
                }
            );
    }, []);

    useEffect(() => {
        // fetch data
      }, []);

    const data = Object.values(items);

    if (error) {
        return <>{error.message}</>;
    } else if (!loaded) {
        return <>loading...</>;
    } else {
        return (
            <>
            <div className="wrapper">
            <ul className="card-grid">
              {data.map((item) => (
                <li key={item.alpha3Code}>
                  <article className="card">
                    <div className="card-image">
                      <img src={item.flag.large} alt={item.name} />
                    </div>
                    <div className="card-content">
                      <h2 className="card-name">{item.name}</h2>
                      // other card content
                    </div>
                  </article>
                </li>
              ))}
            </ul>
          </div>
          </>
    );
    }
}
